//Para la ruta /control-panel del botón  ÁREA ADMIN
import { Link } from "react-router-dom";

const Admin = () => {
  let [nombre, setNombre] = useState("");
  let [contrasenya, setContrasenya] = useState("");

  function comprobarNombre() {
    setNombre(nombre);
    console.log(nombre);
  }

  function comprobarContrasenya() {
    setContrasenya(contrasenya);
    console.log(contrasenya);
  }

  function validar() {
    return "validar";
  }
  return (
    <>
      <div className="col-lg-12 div_2">
        <label for="nombre">Votre nom</label>
        <input
          type="text"
          className="form-control input_size"
          name="nombre"
          onChange={handleInputChange}
        />
      </div>
      <div className="usuario">
        <input
          type="text"
          placeholder="Nombre de Usuario"
          value={nombre}
          onChange={comprobarNombre}
        />
        <input
          type="text"
          placeholder="Contraseña"
          value={contrasenya}
          onChange={comprobarContrasenya}
        />
        <Link to="/control-panel">
          <button onClick={validar}>ÁREA ADMIN</button>
        </Link>
      </div>
    </>
  );
};

export default Admin;
