import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const DetalleProducto = () => {
  const [data, setData] = useState([]);

  const { id } = useParams();
  useEffect(() => {
    fetch(`/Boutique/"${id}`)
      .then((res) => res.json())
      .then((data) => setData(data));
  }, []);
};

export default DetalleProducto;
