import { useState, useEffect } from "react";
import MostrarProducto from "./MostrarProducto";

const ReadProducts = () => {
  const [data, setData] = useState([]);
  const [nombre, setNombre] = useState("");
  useEffect(() => {
    fetch("http://localhost:3001/productos/")
      .then((res) => res.json())
      .then((data) => setData(data));
  }, []);

  const mostrarProductos = data.map((producto, index) => {
    return (
      <div className="cont_pro_admin">
        <div key={producto.nombre} className="productos1">
          <img
            src={producto.imagenes[0]}
            alt={producto.nombre}
            className="imagen"
            onClick={() => {
              setNombre(producto.nombre);
            }}
          />
          <h2>{producto.nombre}</h2>
        </div>
        <button className="btn_modi" type="submit">
          Modifier
        </button>
        <button className="btn_sup" type="submit">
          Supprimier
        </button>
      </div>
    );
  });

  return (
    <div>
      {mostrarProductos}
      <MostrarProducto nombre={nombre} />
    </div>
  );
};

export default ReadProducts;
