import { useState, useEffect } from "react";
import { BrowserRouter, Route } from "react-router-dom";
import SingleMain from "../single_product/SingleMain";
const Boutique = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3001/productos/")
      .then((res) => res.json())
      .then((data) => setData(data));
  });

  const mostrarProductos = data.map((producto) => {
    function verProducto() {
      return (
        <BrowserRouter>
          <Route exact path="/Single_Main">
            <SingleMain />;
          </Route>
        </BrowserRouter>
      );
    }
    return (
      <div>
        <div key={producto.nombre} className="productos" onClick={verProducto}>
          <img
            src={producto.imagenes[0]}
            alt={producto.nombre}
            height="250px"
            className="imagen"
          />
          <h2>{producto.nombre}</h2>
          <p>A partir de {producto.precio} â‚¬</p>
        </div>
      </div>
    );
  });

  return mostrarProductos;
};

export default Boutique;
