import { useState, useEffect } from "react";
import MostrarProducto from "./MostrarProducto";

const ReadProducts = () => {
  const [data, setData] = useState([]);
  useEffect(() => {
    fetch("http://localhost:3001/productos/")
      .then((res) => res.json())
      .then((data) => setData(data));
  });

  const mostrarProductos = data.map((producto) => {
    //FUNCIÓN mostrarProducto a la que llamo con un onClick dentro de la etiqueta imagen y que envía por parámetros el nombre del producto al componente MostrarProducto.js, que lo recibirá por props y que lo pintará en pantalla.
    function mostrarProducto() {
      return <MostrarProducto nombre={producto.nombre} />;
    }
    return (
      <div className="cont_pro_admin">
        <div key={producto.nombre} className="productos1">
          <img
            src={producto.imagenes[0]}
            alt={producto.nombre}
            className="imagen"
            onClick={mostrarProducto}
          />
          <h2>{producto.nombre}</h2>
        </div>
        <button className="btn_modi" type="submit">
          Modifier
        </button>
        <button className="btn_sup" type="submit">
          Supprimier
        </button>
      </div>
    );
  });

  return mostrarProductos;
};

export default ReadProducts;
