import { BrowserRouter, Route } from "react-router-dom";

import { useState } from "react";

const ComprobarUser = () => {
  //Dos variables de estado para usuario y contraseña
  const [usuario, setUsuario] = useState("");
  const [pass, setPass] = useState("");

  //+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
  //Función comprobar a la que llamo cuando le hago click en el botón ENTRER
  //Lo que hace la función es enviar a la DB por el método post el nombre y el usuario introducidos por el usuario

  //TODO AHORA TENDRÍA QUE COMPARAR ESTOOS DATOS CON EL NOMBRE Y USUARIO QUE TENGO EN MI DB PARA COMPROBAR SI COINCIDEN Y DAR ACCESO AL PANEL DE CONTROL.
  //TODO ESTE código queda pendiente.
  // const comprobar = () => {
  //   fetch("", {
  //     method: "POST",
  //     headers: {
  //       "Content-Type": "application/json",
  //     },
  //     body: JSON.stringify({ usuario: usuario, password: pass }),
  //   })
  //     .then((res) => res.json())
  //     .then((res) => {});
  // };
  //+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++

  //Las dos variable de estado se cambian cada vez que el usuario escribe en los inputs
  const escribirNombre = (e) => {
    setUsuario(e.target.value);
  };

  const escribirContrasenya = (e) => {
    setPass(e.target.value);
  };

  //Voy a crear la función comprobar con una condición como en el proyecto del módulo 2.
  const comprobar = (usuario, pass) => {
    if (usuario === "Manu" && pass === "12345") {
      <BrowserRouter>
        <Route exact path="/Kalypse_admin_area">
          <ModalAdmin />
        </Route>
      </BrowserRouter>;
    }
  };
  return (
    <div className="form_admin">
      <h2>ZONE D' ADMINISTRATION</h2>
      <br />
      <label for="nombre">Votre nom</label>
      <input
        type="text"
        className="form-control input_size2"
        value={usuario}
        onChange={escribirNombre}
      />

      <label for="nombre">Votre signature</label>
      <input
        type="text"
        className="form-control input_size2"
        value={pass}
        onChange={escribirContrasenya}
      />
      <button className="btn_admin video" onClick={comprobar}>
        ENTRER
      </button>
    </div>
  );
};

export default ComprobarUser;
