import { useState } from "react";
import { useParams } from "react-router-dom";

const Update = (props) => {
  return (
    <div>
      <h2>CRÉER PRODUIT</h2>
      <input
        className="form-control imput_c"
        type="text"
        placeholder="Nombre"
        value={nombre}
        onChange={introNombre}
      />
      <input
        className="form-control imput_c"
        type="text"
        placeholder="Precio"
        value={precio}
        onChange={introPrecio}
      />
      <input
        className="form-control imput_c"
        type="text"
        placeholder="Wetsuit / Shorty / Top"
        value={tipo}
        onChange={introTipo}
      />
      <input
        className="form-control imput_c"
        type="text"
        placeholder="Homme / Femme"
        value={genero}
        onChange={introGenero}
      />
      <input
        className="form-control imput_c"
        type="text"
        placeholder="3mm / 5mm / 7mm"
        value={grosores}
        onChange={introGrosores}
      />
      <input
        className="form-control imput_c"
        type="text"
        placeholder="XS / S / M / L / XL"
        value={tallas}
        onChange={introTallas}
      />
      <textarea
        className="form-control imput_c"
        placeholder="Descripción"
        value={descripcion}
        onChange={introDescripcion}
      ></textarea>
      <textarea
        className="form-control imput_c"
        placeholder="Design"
        value={design}
        onChange={introDesign}
      ></textarea>
      <textarea
        className="form-control imput_c"
        placeholder="Conception"
        value={conception}
        onChange={introConception}
      ></textarea>
      <textarea
        className="form-control imput_c"
        placeholder="Confort"
        value={confort}
        onChange={introConfort}
      ></textarea>
      <textarea
        className="form-control imput_c"
        placeholder="Imágenes"
        value={imagenes}
        onChange={introImagenes}
      ></textarea>
      <button onClick={anyadir} className="btn_admin video">
        AJOUTER UN PRODUIT
      </button>
    </div>
  );
};
export default Update;
