import { useState, useEffect } from "react";

const MostrarProducto = () => {
  const [data, setData] = useState([]);
  useEffect(() => {
    fetch("http://localhost:3001/single_product/")
      .then((res) => res.json())
      .then((data) => setData(data));
  });

  const mostrarProductos = data.map((producto) => {
    return (
      <div className="cont_pro_admin">
        <div key={producto.nombre} className="productos1">
          <img
            src={producto.imagenes[0]}
            alt={producto.nombre}
            className="imagen"
          />
          <h2>{producto.nombre}</h2>
        </div>
        <button className="btn_modi" type="submit">
          Modifier
        </button>
        <button className="btn_sup" type="submit">
          Supprimier
        </button>
      </div>
    );
  });

  return mostrarProductos;
};

export default MostrarProducto;
