//Para la ruta /control-panel del botón  ÁREA ADMIN
import { BrowserRouter, Route } from "react-router-dom";
import { Link } from "react-router-dom";

import { useState } from "react";
import AdminPage from "../crudProductos/AdminPage";

const Admin = () => {
  let [nombre, setNombre] = useState("");
  let [contrasenya, setContrasenya] = useState("");

  function comprobarNombre() {
    setNombre(nombre);
    console.log(nombre);
  }

  function comprobarContrasenya() {
    setContrasenya(contrasenya);
    console.log(contrasenya);
  }

  function validar() {
    return (
      <BrowserRouter>
        <Route exact path="/Control_panel">
          <AdminPage />;
        </Route>
      </BrowserRouter>
    );
  }
  return (
    <div className="form_admin">
      <label for="nombre">Votre nom</label>
      <input
        type="text"
        className="form-control input_size2"
        value={nombre}
        onChange={comprobarNombre}
      />

      <label for="nombre">Votre signature</label>
      <input
        type="text"
        className="form-control input_size2"
        value={contrasenya}
        onChange={comprobarContrasenya}
      />
      <Link to="/Control_panel">
        <button onClick={validar} className="btn_admin video">
          AREA ADMIN
        </button>
      </Link>
    </div>
  );
};

export default Admin;
