import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

import SingleProductImg from "./SingleProductImg";

const SingleMain = () => {
  const { id } = useParams();
  useEffect(() => {
    fetch("/Boutique/" + id);
  }, []);
  return (
    <div className="sp_cont1">
      <div className="sp_cont2">
        <SingleProductImg />
      </div>
      <div className="sp_cont3">SIDEBAR</div>
    </div>
  );
};

export default SingleMain;
