import SingleMain from "../";

const Boutique = () => {
  const [data, setData] = useState([]);

  // useEffect(function () {
  //   fetch("http://localhost:3001/productos/")
  //     .then(function (results) {
  //       return results.json();
  //     })
  //     .then(function (data) {
  //       //console.log(data);
